# Load existing data
with open("data/grace_products_clean.json") as f:
    existing = json.load(f)

existing_skus = {(item.get("websiteSku") or "").lower().strip() for item in existing}

print(f"Total scraped from accessories page: {len(scraped)}")
print(f"Existing items in grace_products_clean: {len(existing)}")
print()

new_items = []
already_present = []

for item in scraped:
    sku_key = (item["websiteSku"] or "").lower().strip()
    if sku_key in existing_skus:
        already_present.append(item["websiteSku"])
    else:
        new_items.append(item)

print(f"Already in grace_products_clean: {len(already_present)}")
if already_present:
    print("  ->", already_present)

print(f"\nNEW items to add: {len(new_items)}")
print()

# Breakdown by family
from collections import Counter
family_counts = Counter(i["family"] for i in new_items)
print("Breakdown by product family:")
for fam, count in sorted(family_counts.items()):
    print(f"  {fam}: {count}")

print()
print("Items with 10pc pricing:", sum(1 for i in new_items if i.get("webPrice10pc")))
print("Items WITHOUT 10pc pricing (droppers only):", sum(1 for i in new_items if not i.get("webPrice10pc")))

# Merge into existing and save
merged = existing + new_items
with open("data/grace_products_clean.json", "w") as f:
    json.dump(merged, f, indent=2)

print(f"\nâœ… Saved {len(merged)} total items to data/grace_products_clean.json")
print(f"   ({len(new_items)} new items added)")
